function confirmPay(i){var a=$(".zyy-bank-item-selected");if(0==a.length)return void TipUtil.tooltipFun(i,"请选择银行卡");var n=$("#orderId").val(),e=a.find(".agreeNo").val(),o=a.find(".cardNo").val(),t=a.find(".acctName").val(),c=a.find(".idNo").val(),d="/wish/pay/unionPay?offerListId="+n+"&noAgree="+e+"&cardNo="+o+"&acctName="+t+"&idNo="+c;window.location.href=d}$(function(){$(".zyy-bank-item").click(function(){$(".zyy-bank-item").removeClass("zyy-bank-item-selected"),$(this).addClass("zyy-bank-item-selected")}),$(".confirm-pay").click(function(){confirmPay(this)})});