function confirmPay(i){var n=$(".zyy-bank-item-selected");if(0==n.length)return void TipUtil.tooltipFun(i,"请选择银行卡");var a=$("#orderSn").val(),e=n.find(".agreeNo").val(),t=n.find(".cardNo").val(),o=n.find(".acctName").val(),c=n.find(".idNo").val(),d="/part/pay/unionPay?sn="+a+"&noAgree="+e+"&cardNo="+t+"&acctName="+o+"&idNo="+c;window.location.href=d}$(function(){$(".zyy-bank-item").click(function(){$(".zyy-bank-item").removeClass("zyy-bank-item-selected"),$(this).addClass("zyy-bank-item-selected")}),$(".confirm-pay").click(function(){confirmPay(this)})});